sys_script_client.script:
function onSubmit() {
//Type appropriate comment here, and begin script below

//Test if resolved (state 6)
	if(g_form.getValue("state")== 6){

		//Test if close notes is filled with 5 or more words

		var closeNotes = g_form.getValue("close_notes");

		//count spaces in string
		var spaces = closeNotes.split(" ").length;

		if(spaces >= 4)
		{

			//log spaces
			alert(spaces);

			//final message
			alert("Incident Resolved");
		}
		else{

			//log spaces
			alert(spaces);

			//return false
			alert("Not RESOLVED!!!!");
			return false;
		}
	}

}
